  `ip` varchar(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_520_ci NULL DEFAULT NULL,
  `port` smallint(5) UNSIGNED NOT NULL DEFAULT 0,
  `uploaded` bigint(20) UNSIGNED NOT NULL DEFAULT 0,
  `downloaded` bigint(20) UNSIGNED NOT NULL DEFAULT 0,
  `realdownload` bigint(20) UNSIGNED NULL DEFAULT 0,
  `realupload` bigint(20) UNSIGNED NULL DEFAULT 0,
  `to_go` bigint(20) UNSIGNED NOT NULL DEFAULT 0,
  `seedtime` bigint(10) UNSIGNED NOT NULL DEFAULT 0,
  `leechtime` bigint(10) UNSIGNED NOT NULL DEFAULT 0,
  `last_action` datetime NOT NULL,
  `startdat` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `completedat` datetime NOT NULL,
